if (!me.processing_wupServiceCallTimedQueueProcessorTS) {
    me.processing_wupServiceCallTimedQueueProcessorTS = true;
    try{
        // -- Copied from wupUtilsHelpers.DeserializeJSON
        // -- As when you return a JSON it loses INFOTABLES and all...
        function normalizeObject(data,dataTimeFields) {
            var newObj = {};
            for (var key in data) {
                if (data[key].baseType) {
                    newObj[key] = data[key].value;
                } else {
                    if (dataTimeFields[key]) {
                        newObj[key] = new Date(data[key]);
                    } else {
                        newObj[key] = data[key];
                    }
                }
            }
            return newObj;
        }

        function deserializeJSON(jsonString) {
            if (jsonString==="undefined") return null;
            var json = JSON.parse(jsonString);

            // -- Let's correct VARIANT and DATETIME data Types that will not be parsed correctly
            var it,ds,rows,baseType,infotableColumns,itc,rowsc,dsc,columnName,tt,baseTypeC;
            var dtFields = {},dtcFields;
            for (var key in json) {
                if (json[key].dataShape) {
                    it = Resources["InfoTableFunctions"].CreateInfoTable({infoTableName : "InfoTable"});
                    try {
                        ds = json[key].dataShape;
                        rows = json[key].rows;
                        if (rows.length) { // We ensure to have at least 1 row ( to infer BaseType in case of Variant columns
                            infotableColumns = [];
                            for (var field in ds.fieldDefinitions) {
                                baseType = ds.fieldDefinitions[field].baseType;
                                it.AddField({ name: field, baseType: ds.fieldDefinitions[field].baseType });
                                if (baseType==="VARIANT") {
                                    // itc.AddField({ name: field, baseType: rows[0][field].baseType });
                                    it.AddField({ name: field, baseType: rows[0][field].baseType });
                                } else {
                                    // itc.AddField({ name: field, baseType: baseType });
                                    it.AddField({ name: field, baseType: baseType });
                                }
                                if (baseType==="INFOTABLE") {
                                    infotableColumns.push(ds.fieldDefinitions[field]);
                                }
                                if (baseType==="DATETIME") {
                                    dtFields[field] = true;
                                }
                            }
                            for (var i=0;i<rows.length;i++) {
                                // -- Parse Embeded InfoTables.
                                for (var j=0;j<infotableColumns.length;j++) {
                                    columnName =infotableColumns[j].name;
                                    itc = Resources["InfoTableFunctions"].CreateInfoTable({infoTableName : "InfoTable"});
                                    try{
                                        rowsc = rows[i][columnName].rows;
                                        dsc = rows[i][columnName].dataShape;
                                        if (rowsc.length) { // We ensure to have at least 1 row ( to infer BaseType in case of Variant columns
                                            // -- Let's check if there's any Variant Field, and kick it off and replace with
                                            // -- First Row value BaseType
                                            dtcFields = {};
                                            for (var field in dsc.fieldDefinitions) {
                                                baseTypeC = dsc.fieldDefinitions[field].baseType;
                                                // -- As Variant kicks our ass, we infer DataType from first row,
                                                // -- If we have a freacking data with different baseTypes on the same
                                                // -- Variant column it will crash, but this is far from probable.
                                                if (baseTypeC==="VARIANT") {
                                                    itc.AddField({ name: field, baseType: rowsc[0][field].baseType });
                                                } else {
                                                    itc.AddField({ name: field, baseType: baseTypeC });
                                                }
                                                if (baseTypeC==="INFOTABLE") {
                                                    logger.error("[wupUtilHelpers].DeserializeJSON We don't support third level of embbeded Infotables :( !!!!!");
                                                }
                                                if (baseTypeC==="DATETIME") {
                                                    dtcFields[field] = true;
                                                }
                                            }
                                            for (var k=0;k<rowsc.length;k++) {
                                                itc.AddRow(normalizeObject(rowsc[k],dtcFields));
                                            }
                                        }
                                    }catch(err){
                                        // -- We can have an empty InfoTable column, which exist on definition, but doesn't exist as column.
                                        // -- On that case we will ignore all the processing and add an empty InfoTable.
                                        // logger.info("We don't have data for Infotable Column1: "+columnName+" ERROR "+err);

                                    }
                                    rows[i][columnName] = itc;
                                }
                                it.AddRow(normalizeObject(rows[i],dtFields));
                            }
                        }
                    } catch(err){
                        // -- We can have an empty InfoTable column, which exist on definition, but doesn't exist as column.
                        // -- On that case we will ignore all the processing and add an empty InfoTable.
                        // logger.info("We don't have data for Infotable Column2: "+key+" ERROR "+err);
                    }
                    json[key] = it;
                }
            }

            return normalizeObject(json,{});
        }

        // --- Service Code --------

        if (!now) { now = new Date(); }

        // -- Query pending entries
        var pending = me.QueryDataTableEntries({
            maxItems: 1000000,
            values: undefined,
            source: undefined,
            tags: undefined,
            query: {
                "filters": {
                    "fieldName": "callAt",
                    "type": "LE",
                    "value": now
                },
                "sorts": [
                    {
                        "fieldName": "callAt",
                        "isAscending": true
                    }
                ]
            }
        });


        var nVpending = pending.rows.length;
        if (nVpending) {
            // -- Let's delete all the pending entries we are going to process
            var entry;
            var toCall = [];
            for (var i=0;i<nVpending;i++) {
                entry = pending.rows[i];
                try {
                    me.DeleteDataTableEntryByKey({ key: entry.key });
                    toCall.push(entry);
                } catch(err) {
                    logger.error("[wupServiceCallTimedQueueProcessorTS("+me.name+")].ProcessPendingServiceCalls: Failed trying to delete pending service call ["+entry.thing+"."+entry.service+"] with Key ["+entry.key+"], the corresponding service will not be called. ERROR: "+err);
                }
            }


            // -- Let's do the service calls
            var params; 
            for (var i=0;i<toCall.length;i++) {
                entry = toCall[i];
                try {
                    params = (entry.paramsString?deserializeJSON(entry.paramsString):null);
                    //logger.info("PARAMS "+params.eventData.rows[0].newValue.rows[0].value);
                    Things[entry.thing][entry.service](params);
                } catch(err) {
                    logger.error("[wupServiceCallTimedQueueProcessorTS("+me.name+")].ProcessPendingServiceCalls: Failed pending service call ["+entry.thing+"."+entry.service+"]. ERROR: "+err);
                }
            }
        }
    } catch(err) {
        logger.error("[wupServiceCallTimedQueueProcessorTS("+me.name+")].ProcessPendingServiceCalls: Some uncaught error happened :(. ERROR: "+err);
    }

  me.processing_wupServiceCallTimedQueueProcessorTS = false;
}

var now = new Date();
var values = Resources["InfoTableFunctions"].CreateInfoTableFromDataShape({
	infoTableName : "InfoTable",
	dataShapeName : "wupServiceCallTimedQueueDS"
});

